
public class Army {
	private int HeroInfantry, HeroCavalry, HeroArcher,
	TroopsInfantry, TroopsCavalry, TroopsArcher;
	public Army() {
		// TODO Auto-generated constructor stub
	}
	public Army(int heroInfantry, int heroCavalry, int heroArcher, int troopsInfantry, int troopsCavalry,
			int troopsArcher) {
		super();
		HeroInfantry = heroInfantry;
		HeroCavalry = heroCavalry;
		HeroArcher = heroArcher;
		TroopsInfantry = troopsInfantry;
		TroopsCavalry = troopsCavalry;
		TroopsArcher = troopsArcher;
	}
	public int getHeroInfantry() {
		return HeroInfantry;
	}
	public void setHeroInfantry(int heroInfantry) {
		HeroInfantry = heroInfantry;
	}
	public int getHeroCavalry() {
		return HeroCavalry;
	}
	public void setHeroCavalry(int heroCavalry) {
		HeroCavalry = heroCavalry;
	}
	public int getHeroArcher() {
		return HeroArcher;
	}
	public void setHeroArcher(int heroArcher) {
		HeroArcher = heroArcher;
	}
	public int getTroopsInfantry() {
		return TroopsInfantry;
	}
	public void setTroopsInfantry(int troopsInfantry) {
		TroopsInfantry = troopsInfantry;
	}
	public int getTroopsCavalry() {
		return TroopsCavalry;
	}
	public void setTroopsCavalry(int troopsCavalry) {
		TroopsCavalry = troopsCavalry;
	}
	public int getTroopsArcher() {
		return TroopsArcher;
	}
	public void setTroopsArcher(int troopsArcher) {
		TroopsArcher = troopsArcher;
	}
	
}
